import React from "react";
import { connect } from 'react-redux';
import 'bootstrap/dist/css/bootstrap.min.css';
//import * as actions from './actions'
import Login from './Login'
import Registration from './Registration'
import {gotoprofile, requestdata, updateheadline, userlogout} from "../../actions";

class Landing extends React.Component{
    constructor(props) {
        super(props);

        this.errorMessage = '';
        this.state = {
            redirect:false,
            userId:""
        }
    }
    render() {
        return (
            <div>
                <div className="jumbotron text-center">
                    <h1> Welcome to RiceZone!</h1>
                </div>

                <div className="row">
                    <Registration/>
                    <Login/>
                </div>

            </div>

        )
    }
}
const mapStateToProps = (state) => {

    return {
        userId:state.userId,
        headline:state.headline,
        baseinfo:state.baseinfo,
        data:state.data,
        friendlist: state.friendlist,
        articles:state.articles
    }
};

const mapDispatchToProps = (dispatch) => {
    return {
        requestdata: (data)=> dispatch(requestdata(data)),
        userlogout: () =>dispatch(userlogout()),
        updateheadline:(headline)=>dispatch(updateheadline(headline)),
        gotoprofile: (input)=>dispatch(gotoprofile(input))

    }
};

export default connect(mapStateToProps,mapDispatchToProps)(Landing
)
