import React from "react";
import { connect } from 'react-redux';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Redirect} from "react-router-dom";
import {userlogout,updateheadline,gotoprofile} from "../../actions";
import Avatar from 'react-avatar';
import './img.css';
import {bigimg, smallimg} from "../../actions";


class Main extends React.Component {
    constructor(props) {
        super(props);
        this.handleProfile = this.handleProfile.bind(this);
        this.handleLogout = this.handleLogout.bind(this);
        this.handleHeadline = this.handleHeadline.bind(this);
        this.handleClear = this.handleClear.bind(this);
        this.getfriends = this.getfriends.bind(this);
        this.getarticles = this.getarticles.bind(this);
        this.handlecomments = this.handlecomments.bind(this);
        this.handlefriends = this.handlefriends.bind(this);
        this.handleunfriends = this.handleunfriends.bind(this);
        this.handlePost = this.handlePost.bind(this);
        this.search = this.search.bind(this);



        this.state = {
            userId: this.props.userId,
            friendlist: this.props.friendlist,
            baseinfo: this.props.baseinfo,
            data: this.props.data,
            toProfile: false,
            toLanding: false,
            headline: localStorage.getItem(this.props.userId) === null ?this.props.headline:localStorage.getItem(this.props.userId),
            articles: this.props.articles,
            searchcontent : [],
            userscomment:[]

        }
        this.errorMessage='';
        let temp = this.props.usergroup
        this.theuser = temp.find(element=>element[0]===this.state.userId)
        if(this.theuser === undefined) {
            this.hardcodeId = 1;
        } else {
            this.hardcodeId = this.theuser[5];
        }

        }



        componentDidMount()
        {   //fetch(`https://jsonplaceholder.typicode.com/posts`).then(res => res.json()).then(res =>
            //this.props.requestdata(res));
            let a = () => {
                    this.setState({data: this.props.data});
                    if(this.state.friendlist.length == 0) {

                        this.getfriends(this.props.data, this.hardcodeId);
                        this.getarticles(this.props.data);

                        }
                    }
            setTimeout(a, 500)

        }

        handlecomments(e){
            e.preventDefault();
            let commentid = e.target.id.split(',');
            let id = parseInt(commentid[0]);
            let user = commentid[1]
            console.log(this.state.userscomment)


        }

        getarticles(data){
            let temp1 = [];
            let temp2 = [];
            let temp = this.state.friendlist;
            let usergroup = this.props.usergroup;
            let theuser = this.props.userId;
            data.forEach(function(e){
                let a = e.id;
                let poster = usergroup.find(element=>(element[5]==e.userId))
                let isUser = poster[0]==theuser
                let isFriend = temp.includes(poster[0])
                if(!temp2.includes(a) && (isUser || isFriend)) {
                    temp2.push(a);
                    temp1.push({...e,userId:poster[0]});
                }
            });
            let commentstatus = new Array(temp1.length).fill(false);
            this.setState({userscomment:commentstatus})
            this.setState({articles:temp1});

        }

        getfriends(data,id){
                let temp = [];
                let temp1 = [];
                let usergroup = this.props.usergroup;

                data.forEach(function(e){
                    let a = e.userId;

                    if((!temp.includes(a)&&(a!==id)&&(e.userId<(parseInt(id)+4)))){
                        temp.push(a);
                        let user = usergroup.find(element=>element[5]==a)
                        temp1.push(user[0])
                    }
                })
            if(id>7){
                let a = 0
                for(;a<(id+3)%10;a++){
                    temp.push(a);
                    let user = this.props.usergroup.find(element=>element[5]==a)
                    temp1.push(user[0])
                }
            }
                this.setState({friendlist:temp1});
        }

        handlePost(e){
            e.preventDefault();
            let ntitle = document.getElementById("posttitle").value;
            let npost = document.getElementById("newpost").value;
            let temp = [...this.state.articles];
            let time = new Date(Date.now()).toDateString();
            temp.unshift({title:ntitle,body:npost,userId:this.state.userId,timestamp:time});
            this.setState({articles:temp});
            document.getElementById("posttitle").value='';
            document.getElementById("newpost").value='';

        }
        handleProfile(e)
        {
            e.preventDefault();
            let temp = {friendlist: this.state.friendlist,articles: this.state.articles}
            this.props.gotoprofile(temp);
            this.setState({toProfile: true});
        }
        handleLogout(e)
        {
            e.preventDefault();
            this.props.userlogout();
            this.setState({toLanding: true})
        }

        handleHeadline(e)
        {
            e.preventDefault();
            let headline = document.getElementById('headlinetext').value
            this.props.updateheadline(headline);
            //setTimeout(() => {
                this.setState({headline: headline})
            //}, 100);


            localStorage.setItem(this.props.userId,headline)
            document.getElementById('headlinetext').value = '';

        }

        handleClear(e)
        {
            e.preventDefault();
            document.getElementById('newpost').value = '';
            document.getElementById('posttitle').value = '';
        }

        handlefriends(e){
            e.preventDefault();
            this.errorMessage = ''
            let a = document.getElementById('newfriend').value;
            let adding =  this.props.usergroup.find(element=>element[0]==a)
            if(a!='' && !this.state.friendlist.includes(a) && a!=this.props.userId && !(adding === undefined)) {
                let temp = [...this.state.friendlist];
                temp.push(document.getElementById('newfriend').value);
                this.setState({friendlist: temp});
                document.getElementById('newfriend').value = '';
            } else if (this.state.friendlist.includes(a) || a == this.props.userId){
                this.errorMessage = 'Cannot add followed user or yourself!'
            } else if (adding === undefined){
                this.errorMessage = 'Not an existing user!'
            }
            this.setState({errorMessage:this.errorMessage})
            setTimeout(()=>{this.getarticles(this.props.data)},50);

        }

        handleunfriends(e){
            e.preventDefault()
            let temp = [...this.state.friendlist];
            let theindex = temp.indexOf(e.target.id);
            temp.splice(theindex,1);
            this.setState({friendlist: temp});
            setTimeout(()=>{this.getarticles(this.props.data)},50);
        }

        search(event){
            event.preventDefault();
            let temp = [];
            this.state.articles.forEach(function(e){
                if (event.target.value == e.userId){
                    temp.unshift(e);
                }
            })
            if(event.target.value==''){temp=[];}
            this.setState({searchcontent:temp});

        }


        render()
        {
            if (this.state.toProfile) {
                return (

                    <Redirect to='/Profile'/>

                )
            }

            if (this.state.toLanding) {
                return (
                    <Redirect to='/'/>
                )
            }

            return (
                <div>

                    <div className="jumbotron text-center">
                        <h1> Hello! {this.state.userId}!Welcome to your zone!</h1>
                    </div>
                    <div className='row'>
                        <div className="col-lg-4 col-md-4 col-sm-4 col-xs-4">
                            <div className='card'>

                                <div className='row'>
                                    <div className='col-md-6'>
                                        <button className="btn btn-primary" type='submit'
                                                onClick={this.handleLogout}>Logout
                                        </button>
                                    </div>
                                    <div className='col-md-offset-4 col-md-6'>
                                        <button className="btn btn-primary" type='submit'
                                                onClick={this.handleProfile}>Profile
                                        </button>
                                    </div>
                                </div>

                                <div className='row'>
                                    <div className='col-md-12'>
                                        <Avatar round src={smallimg}/>
                                        <p className="font-weight-lighter">{this.state.userId}</p>
                                    </div>
                                </div>

                                <div className='row'>
                                    <div className='col-md-12'>
                                        <p className="font-weight-bold">Status: {this.state.headline}</p>
                                    </div>
                                </div>

                                <div className='row'>
                                    <div className='col-md-12'>
                                        <input type='text' id='headlinetext'/>
                                        <br/>
                                        <button type='submit' onClick={this.handleHeadline}>Change</button>

                                    </div>
                                </div>

                            </div>
                        </div>
                        <div className="col-lg-8 col-md-8 col-sm-8 col-xs-8">
                            <div className='card'>
                                <div className='row'>
                                    <div className='col-md-12'>
                                        <textarea className="form-control" rows="1" id='posttitle'
                                                  placeholder='Post title'></textarea>
                                    <textarea className="form-control" rows="5" id='newpost'
                                              placeholder='Post your day!'></textarea>
                                    </div>
                                </div>
                                <div className='row'>
                                    <div className='col-md-12'>
                                        <button className="btn btn-primary center" type='submit' onClick={this.handlePost}>Post</button>
                                        <button className="btn btn-primary center" type='submit'
                                                onClick={this.handleClear}>Clear
                                        </button>
                                    </div>
                                </div>

                                <div className='row'>
                                    <div className='col-md-12'>
                                        <p>Upload your picture:<input type='file'/></p>
                                    </div>
                                </div>

                            </div>
                        </div>
                    </div>
                    <div className='row'>
                        <div className='col-md-12'>

                            <p>Search bar: <input type = 'text' onChange={this.search} placeholder='For now, can only search by userId'></input></p>
                            <div className='card'>
                                {this.state.searchcontent == []?'':this.state.searchcontent.map((x,i)=>
                                    <div key={i} className='row col-md-12'>
                                        <p>  The user is {x.userId} and title is {x.title}.</p>
                                        <p>  The post is {x.body}</p>
                                    </div>)}
                            </div>
                        </div>

                    </div>

                    <div className='row'>
                        <div className="col-lg-4 col-md-4 col-sm-4 col-xs-4">
                            <div className='card'>
                                {!(this.errorMessage=== '')? <div className='col-md-12'><div className= 'row alert-danger'>{this.errorMessage}</div></div>:''}
                                {this.state.friendlist.map((x,i)=>
                                    <div key={i} className='row'>
                                        <div className='col-md-12'>
                                            <Avatar round src={smallimg}/>
                                            <p className="font-weight-lighter">user {x}</p>
                                            <p className="font-weight-bold">Status: I am user {x}</p>
                                            <button type='submit' className = 'btn btn-danger' id = {x} onClick={this.handleunfriends}>Unfollow</button>
                                            <br></br>
                                            <br></br>
                                        </div>
                                    </div>)}


                                    <br></br>
                                    <div className = 'row'>
                                        <div className='col-md-12'>
                                            <input type='text' id='newfriend' placeholder = 'add a new friend'/>
                                        </div>
                                        <div className='col-md-12'>
                                            <button type='submit' className = 'btn btn-primary' onClick={this.handlefriends}>Follow</button>
                                        </div>
                                    </div>
                            </div>
                        </div>

                        <div className="col-lg-8 col-md-8 col-sm-8 col-xs-8">
                            <div className='card'>
                                <button type='submit' className = 'btn btn-secondary'>Show Comment</button>
                            </div>
                            <div className='card'>
                                <ul className="list-group list-group-flush">
                                {this.state.articles.map((x,i)=>
                                    <li key={i} className="list-group-item">
                                        <div className='col-md-12'>
                                            <p className="font-weight-bold">{x.title}</p>
                                            <img src={bigimg}/>
                                            <p className="font-italic">{x.body}</p>
                                            <p className="font-weight-bold">Published by: User {x.userId} {x.timestamp === undefined?'': ('on '+String(x.timestamp))}</p>
                                            <button type='submit' className = 'btn btn-secondary'>Edit</button>
                                            <button type='submit' className = 'btn btn-warning' id = {[x.id,x.userId]} onClick={this.handlecomments}>Comment</button>
                                            <br></br>
                                            <br></br>
                                        </div>
                                    </li>)}
                                    </ul>

                            </div>
                        </div>
                    </div>


                </div>

            )

        }

    }


const mapStateToProps = (state) => {

    return {
        userId:state.userId,
        headline:state.headline,
        baseinfo:state.baseinfo,
        data:state.data,
        friendlist: state.friendlist,
        articles:state.articles,
        usergroup:state.usergroup,
        comments:state.comments
    }
};

const mapDispatchToProps = (dispatch) => {
    return {

        userlogout: () =>dispatch(userlogout()),
        updateheadline:(headline)=>dispatch(updateheadline(headline)),
        gotoprofile: (input)=>dispatch(gotoprofile(input))

    }
};


export default connect(mapStateToProps,mapDispatchToProps)(Main)
