import {REGISTER, LOGIN,LOGOUT,REQUESTDATA,UPDATEHEADLINE,GOTOPROFILE,GOTOMAIN,REQUESTUSERGROUP} from "./actions";
async function a() {
    let res = await fetch(`https://jsonplaceholder.typicode.com/comments`).then(res => res.json())
    return res}
    let comment = a()

const intialState = {
    userId: 'dummyuser',
    password:'12345',
    baseinfo:{zipcode:'77025',phonenumber:'123-123-1234',email:'dummy@gmail.com'},
    headline:'Today is a good day!',
    data:[],
    friendlist:[],
    articles:[],
    usergroup:[],
    registeredId:'',
    comments:comment
};

export function Reducer( state = intialState, action){
    switch(action.type){
        case REGISTER:
            return {...state,registeredId:action.userId}
        case LOGIN:
            return {...state,userId:action.userId,password: action.password}
        case REQUESTUSERGROUP:
            return {...state,usergroup:action.usergroup}

        case LOGOUT:
            return {...state,userId: 'dummyuser',
                password:'',
                baseinfo:{zipcode:'77025',phonenumber:'123-123-1234',email:'dummy@gmail.com'},
                headline:'Today is a good day!',
                data:[],
                friendlist:[],
                articles:[]}

        case REQUESTDATA:

            return {...state,data:action.data}

        case UPDATEHEADLINE:
            return {...state,headline:action.headline}

        case GOTOPROFILE:
            return {...state,friendlist: action.input.friendlist,articles:action.input.articles}

        case GOTOMAIN:

            return {...state,userId: action.input.userId,password: action.input.password,baseinfo: action.input.baseinfo}

        default:
            return state

    }
    return state

}
