import React from "react";
import { connect } from 'react-redux';
import 'bootstrap/dist/css/bootstrap.min.css';
//import * as actions from './actions'
import Login from './Login'
import Registration from './Registration'
import {requestusergroup} from "../../actions";

class Landing extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            usergroup: []
        }
    }


    componentDidMount(){
        async function a() {
            let res = await fetch(`https://jsonplaceholder.typicode.com/users`).then(res => res.json())
            console.log(res)
            let temp = [];
            temp = res.map(x=>[x.username,x.address.street,x.phone,x.address.zipcode,x.email] );

            return temp
        }
        let temp = []
        temp = a();
        this.setState({usergroup:temp});

        this.props.requestusergroup(temp);


    }

    render() {
        return (
            <div>
                <div className="jumbotron text-center">
                    <h1> Welcome to RiceZone!</h1>
                </div>

                <div className="row">
                    <Registration/>
                    <Login/>
                </div>

            </div>

        )
    }
}


const mapDispatchToProps = (dispatch) => {
    return {
        requestusergroup: (usergroup)=> dispatch(requestusergroup(usergroup)),


    }
};

export default connect(null,mapDispatchToProps)(Landing
