import {REGISTER, LOGIN,LOGOUT} from "./actions";

const intialState = {
    userId: '',

};

export function Reducer( state = intialState, action){
    switch(action.type){
        case REGISTER:
            return {...state,userId:action.userId}
        case LOGIN:
            return {...state,userId:action.userId}

        case LOGOUT:
            return {...state,userId:''}



        default:
            return state

    }
    return state

}
