import React from "react";
import { connect } from 'react-redux';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Redirect} from "react-router-dom";
import {userlogout,requestdata} from "../../actions";
import Avatar from 'react-avatar';


class Main extends React.Component{
    constructor(props) {
        super(props);
        this.handleProfile = this.handleProfile.bind(this);
        this.handleLogout = this.handleLogout.bind(this);
        this.state = {userId:this.props.userId,
                      friendlist:[],
                      baseinfo:this.props.baseinfo,
                      data: null,
                      toProfile:false,
                      toLanding:false,
                      headline:this.props.headline


        };

    }

    componentDidMount() {
        fetch(`https://jsonplaceholder.typicode.com/posts`).then(res => res.json()).then(res =>

        this.props.requestdata(res));
        let a = ()=>
        {
            this.setState({data:this.props.data});
        }

        setTimeout(a,300)
    }

    handleProfile(e){
        e.preventDefault();
        this.setState({toProfile:true});
    }
    handleLogout(e){
        e.preventDefault();
        this.props.userlogout();
        this.setState({toLanding:true})
    }




    render(){
        if(this.state.toProfile){
            return(

                <Redirect to='/Profile' />

            )
        }

        if(this.state.toLanding){
            return(
                <Redirect to='/' />
            )
        }

        return(
            <div>
                <div className="jumbotron text-center">
                    <h1> Hello! {this.state.userId}!Welcome to your zone!</h1>
                </div>
                <div className = 'row'>
                    <div className = "col-lg-4 col-md-4 col-sm-4 col-xs-4">
                        <div className='card'>

                            <div className='row'>
                            <div className='col-md-6'>
                                <button className="btn btn-primary" type = 'submit' onClick={this.handleLogout}>Logout</button>
                            </div>
                            <div className='col-md-offset-4 col-md-6'>
                                <button className="btn btn-primary" type = 'submit' onClick={this.handleProfile}>Profile</button>
                            </div>
                            </div>

                            <div className= 'row' >
                                <div className= 'col-md-6'>

                                </div>
                                <Avatar src = '/small image.jpeg'></Avatar>
                            </div>

                        </div>
                    </div>
                    <div className = "col-lg-8 col-md-8 col-sm-8 col-xs-8">
                        <div className='card'>
                            <div claassName = 'row'>
                            <textarea className="form-control" rows="5" placeholder='Post your day!'></textarea>
                            </div>
                            <div claassName = 'row'>
                                <div className='col-md-12'>
                                    <button className="btn btn-primary center" type = 'submit'>Post</button>
                                    <button className="btn btn-primary center" type = 'submit'>Clear</button>
                                </div>
                            </div>
                            <div className ='row'>
                                <div className='col-md-12'>
                                    <p>Upload your picture:<input type='file'/></p>
                                </div>

                            </div>

                        </div>
                    </div>
                </div>


            </div>

        )

    }

}

const mapStateToProps = (state) => {

    return {
        userId:state.userId,
        headline:state.headline,
        baseinfo:state.baseinfo,
        data:state.data
    }
};

const mapDispatchToProps = (dispatch) => {
    return {
        requestdata: (data)=> dispatch(requestdata(data)),
        userlogout: () =>dispatch(userlogout())
    }
};


export default connect(mapStateToProps,mapDispatchToProps)(Main)
