import {REGISTER, LOGIN,LOGOUT,REQUESTDATA,UPDATEHEADLINE,GOTOPROFILE} from "./actions";

const intialState = {
    userId: 'dummyuser',
    baseinfo:{zipcodec:'',phonenumber:'',email:''},
    headline:'Today is a good day!',
    data:[],
    friendlist:[],
    articles:[]
};

export function Reducer( state = intialState, action){
    switch(action.type){
        case REGISTER:
            return {...state,userId:action.userId,baseinfo: action.baseinfo}
        case LOGIN:
            return {...state,userId:action.userId}

        case LOGOUT:
            return {...state,userId:''}

        case REQUESTDATA:

            return {...state,data:action.data}

        case UPDATEHEADLINE:
            return {...state,headline:action.headline}

        case GOTOPROFILE:
            return {...state,friendlist: action.input.friendlist,articles:action.input.articles}

        default:
            return state

    }
    return state

}
