import {REGISTER, LOGIN,LOGOUT,REQUESTDATA} from "./actions";

const intialState = {
    userId: '',
    baseinfo:{zipcodec:'',phonenumber:'',email:''},
    headline:'Today is a good day!',
    data:null
};

export function Reducer( state = intialState, action){
    switch(action.type){
        case REGISTER:
            return {...state,userId:action.userId,baseinfo: action.baseinfo}
        case LOGIN:
            return {...state,userId:action.userId}

        case LOGOUT:
            return {...state,userId:''}

        case REQUESTDATA:
            return {...state,data:action.data}

        default:
            return state

    }
    return state

}
