export const LOGIN = 'LOGIN'
export const REGISTER = 'REGISTER'
export const LOGOUT ='LOGOUT'


export const register=(userId,baseinfo)=>{
    return{
        type:REGISTER,
        userId:userId,
        baseinfo:baseinfo
    }
}

export const userlogin=(userId)=>{
    return{
        type:LOGIN,
        userId:userId
    }
}

export const userlogout=()=>{
    return{
        type:LOGIN
    }
}
