import {REGISTER, LOGIN,LOGOUT,REQUESTDATA,UPDATEHEADLINE,GOTOPROFILE,GOTOMAIN} from "./actions";

const intialState = {
    userId: 'dummyuser',
    password:'12345',
    baseinfo:{zipcode:'77025',phonenumber:'123-123-1234',email:'dummy@gmail.com'},
    headline:'Today is a good day!',
    data:[],
    friendlist:[],
    articles:[]
};

export function Reducer( state = intialState, action){
    switch(action.type){
        case REGISTER:
            return {...state,userId:action.userId,baseinfo: action.baseinfo}
        case LOGIN:
            return {...state,userId:action.userId,password: action.password}

        case LOGOUT:
            return {...state,userId:''}

        case REQUESTDATA:

            return {...state,data:action.data}

        case UPDATEHEADLINE:
            return {...state,headline:action.headline}

        case GOTOPROFILE:
            return {...state,friendlist: action.input.friendlist,articles:action.input.articles}

        case GOTOMAIN:

            return {...state,userId: action.input.userId,password: action.input.password,baseinfo: action.input.baseinfo}

        default:
            return state

    }
    return state

}
