import React from "react";
import { connect } from 'react-redux';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Redirect} from "react-router-dom";
import {userlogout,requestdata,updateheadline} from "../../actions";
import Avatar from 'react-avatar';
import './img.css';
import {bigimg, smallimg} from "../../actions";


class Main extends React.Component {
    constructor(props) {
        super(props);
        this.handleProfile = this.handleProfile.bind(this);
        this.handleLogout = this.handleLogout.bind(this);
        this.handleHeadline = this.handleHeadline.bind(this);
        this.handleClear = this.handleClear.bind(this);
        this.getfriends = this.getfriends.bind(this);
        this.handlefriends = this.handlefriends.bind(this);
        this.handleunfriends = this.handleunfriends.bind(this);
        this.state = {
            userId: this.props.userId,
            friendlist: [],
            baseinfo: this.props.baseinfo,
            data: null,
            toProfile: false,
            toLanding: false,
            headline: this.props.headline,
            articles: []
        }
        this.hardcodeId = '1';


        }



        componentDidMount()
        {
            fetch(`https://jsonplaceholder.typicode.com/posts`).then(res => res.json()).then(res =>
                this.props.requestdata(res));

                let a = () => {
                    this.setState({data: this.props.data});

                    this.getfriends(this.state.data,this.hardcodeId);
                    }

            setTimeout(a, 500)
        }

        getfriends(data,id){
                let temp = [];

                data.forEach(function(e){
                    if((!temp.includes(e.userId))&&(e.userId!==id)&&(e.userId<(parseInt(id)+3))){
                        temp.push(e.userId);
                    }
                })
                this.setState({friendlist:temp});
        }

        handleProfile(e)
        {
            e.preventDefault();
            this.setState({toProfile: true});
        }
        handleLogout(e)
        {
            e.preventDefault();
            this.props.userlogout();
            this.setState({toLanding: true})
        }

        handleHeadline(e)
        {
            e.preventDefault();
            this.props.updateheadline(document.getElementById('headlinetext').value);
            setTimeout(() => {
                this.setState({headline: this.props.headline})
            }, 100);
            document.getElementById('headlinetext').value = '';

        }

        handleClear(e)
        {
            e.preventDefault();
            document.getElementById('newpost').value = '';
        }

        handlefriends(e){
            e.preventDefault();
            let a = document.getElementById('newfriend').value;
            if(a!='' && !this.state.friendlist.includes(a)) {
                let temp = [...this.state.friendlist];
                temp.push(document.getElementById('newfriend').value);
                this.setState({friendlist: temp});
                document.getElementById('newfriend').value = '';
            }

        }

        handleunfriends(e){
            e.preventDefault()
            console.log(e.target.id);

        }


        render()
        {
            if (this.state.toProfile) {
                return (

                    <Redirect to='/Profile'/>

                )
            }

            if (this.state.toLanding) {
                return (
                    <Redirect to='/'/>
                )
            }

            return (
                <div>
                    <div className="jumbotron text-center">
                        <h1> Hello! {this.state.userId}!Welcome to your zone!</h1>
                    </div>
                    <div className='row'>
                        <div className="col-lg-4 col-md-4 col-sm-4 col-xs-4">
                            <div className='card'>

                                <div className='row'>
                                    <div className='col-md-6'>
                                        <button className="btn btn-primary" type='submit'
                                                onClick={this.handleLogout}>Logout
                                        </button>
                                    </div>
                                    <div className='col-md-offset-4 col-md-6'>
                                        <button className="btn btn-primary" type='submit'
                                                onClick={this.handleProfile}>Profile
                                        </button>
                                    </div>
                                </div>

                                <div className='row'>
                                    <div className='col-md-12'>
                                        <Avatar round src={smallimg}/>
                                        <p className="font-weight-lighter">{this.state.userId}</p>
                                    </div>
                                </div>

                                <div className='row'>
                                    <div className='col-md-12'>
                                        <p className="font-weight-bold">Status: {this.state.headline}</p>
                                    </div>
                                </div>

                                <div className='row'>
                                    <div className='col-md-12'>
                                        <input type='text' id='headlinetext'/>
                                        <br/>
                                        <button type='submit' onClick={this.handleHeadline}>Change</button>

                                    </div>
                                </div>

                            </div>
                        </div>
                        <div className="col-lg-8 col-md-8 col-sm-8 col-xs-8">
                            <div className='card'>
                                <div className='row'>
                                    <div className='col-md-12'>
                                    <textarea className="form-control" rows="5" id='newpost'
                                              placeholder='Post your day!'></textarea>
                                    </div>
                                </div>
                                <div className='row'>
                                    <div className='col-md-12'>
                                        <button className="btn btn-primary center" type='submit'>Post</button>
                                        <button className="btn btn-primary center" type='submit'
                                                onClick={this.handleClear}>Clear
                                        </button>
                                    </div>
                                </div>

                                <div className='row'>
                                    <div className='col-md-12'>
                                        <p>Upload your picture:<input type='file'/></p>
                                    </div>
                                </div>

                            </div>
                        </div>
                    </div>
                    <div className='row'>
                        <div className='col-md-12'>

                            <p>Search bar</p>
                        </div>

                    </div>

                    <div className='row'>
                        <div className="col-lg-4 col-md-4 col-sm-4 col-xs-4">
                            <div className='card'>
                                {this.state.friendlist.map((x,i)=>
                                    <div key={i} className='row'>
                                        <div className='col-md-12'>
                                            <Avatar round src={smallimg}/>
                                            <p className="font-weight-lighter">user {x}</p>
                                            <p className="font-weight-bold">Status: I am user {x}</p>
                                            <button type='submit' className = 'btn btn-danger' id = {x} onClick={this.handleunfriends}>Unfollow</button>
                                        </div>
                                    </div>)}

                                    <div className = 'row'>
                                    </div>

                                    <div className = 'row'>
                                        <div className='col-md-12'>
                                            <input type='text' id='newfriend' placeholder = 'add a new friend'/>
                                        </div>
                                        <div className='col-md-12'>
                                            <button type='submit' className = 'btn btn-primary' onClick={this.handlefriends}>Follow</button>
                                        </div>
                                    </div>
                            </div>
                        </div>

                        <div className="col-lg-8 col-md-8 col-sm-8 col-xs-8">

                        </div>
                    </div>


                </div>

            )

        }

    }


const mapStateToProps = (state) => {

    return {
        userId:state.userId,
        headline:state.headline,
        baseinfo:state.baseinfo,
        data:state.data
    }
};

const mapDispatchToProps = (dispatch) => {
    return {
        requestdata: (data)=> dispatch(requestdata(data)),
        userlogout: () =>dispatch(userlogout()),
        updateheadline:(headline)=>dispatch(updateheadline(headline))

    }
};


export default connect(mapStateToProps,mapDispatchToProps)(Main)
