import React from "react";
import { connect } from 'react-redux';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Redirect} from "react-router-dom";
import {userlogout,requestdata,updateheadline} from "../../actions";
import Avatar from 'react-avatar';
import './img.css';
import {bigimg, smallimg} from "../../actions";


class Main extends React.Component {
    constructor(props) {
        super(props);
        this.handleProfile = this.handleProfile.bind(this);
        this.handleLogout = this.handleLogout.bind(this);
        this.handleHeadline = this.handleHeadline.bind(this);
        this.handleClear = this.handleClear.bind(this);
        this.getfriends = this.getfriends.bind(this);
        this.getarticles = this.getarticles.bind(this);
        this.handlefriends = this.handlefriends.bind(this);
        this.handleunfriends = this.handleunfriends.bind(this);
        this.state = {
            userId: this.props.userId,
            friendlist: this.props.friendlist,
            baseinfo: this.props.baseinfo,
            data: null,
            toProfile: false,
            toLanding: false,
            headline: this.props.headline,
            articles: this.props.articles
        }
        this.hardcodeId = '1';


        }



        componentDidMount()
        {
            fetch(`https://jsonplaceholder.typicode.com/posts`).then(res => res.json()).then(res =>
                this.props.requestdata(res));

                let a = () => {
                    this.setState({data: this.props.data});

                    this.getfriends(this.state.data,this.hardcodeId);
                    this.getarticles(this.state.data,this.hardcodeId);
                    }

            setTimeout(a, 500)
        }

        getarticles(data,id){
            let temp1 = [];
            let temp2 = [];
            let temp = this.state.friendlist;
            data.forEach(function(e){
                let a = String(e.id);
                if(!temp2.includes(a) && (e.userId==id || temp.includes(String(e.userId)))){
                    temp2.push(a);
                    temp1.push(e);
                }
            });
            this.setState({articles:temp1});

        }

        getfriends(data,id){
                let temp = [];

                data.forEach(function(e){
                    let a = String(e.userId);
                    if((!temp.includes(a)&&(a!==id)&&(e.userId<(parseInt(id)+4)))){
                        temp.push(a);
                    }
                })
                this.setState({friendlist:temp});
        }

        handleProfile(e)
        {
            e.preventDefault();
            this.setState({toProfile: true});
        }
        handleLogout(e)
        {
            e.preventDefault();
            this.props.userlogout();
            this.setState({toLanding: true})
        }

        handleHeadline(e)
        {
            e.preventDefault();
            this.props.updateheadline(document.getElementById('headlinetext').value);
            setTimeout(() => {
                this.setState({headline: this.props.headline})
            }, 100);
            document.getElementById('headlinetext').value = '';

        }

        handleClear(e)
        {
            e.preventDefault();
            document.getElementById('newpost').value = '';
        }

        handlefriends(e){
            e.preventDefault();
            let a = document.getElementById('newfriend').value;
            if(a!='' && !this.state.friendlist.includes(a)) {
                let temp = [...this.state.friendlist];
                temp.push(document.getElementById('newfriend').value);
                this.setState({friendlist: temp});
                document.getElementById('newfriend').value = '';
            }

        }

        handleunfriends(e){
            e.preventDefault()
            let temp = [...this.state.friendlist];


            let theindex = temp.indexOf(e.target.id);

            temp.splice(theindex,1);

            this.setState({friendlist: temp});

        }


        render()
        {
            if (this.state.toProfile) {
                return (

                    <Redirect to='/Profile'/>

                )
            }

            if (this.state.toLanding) {
                return (
                    <Redirect to='/'/>
                )
            }

            return (
                <div>
                    <div className="jumbotron text-center">
                        <h1> Hello! {this.state.userId}!Welcome to your zone!</h1>
                    </div>
                    <div className='row'>
                        <div className="col-lg-4 col-md-4 col-sm-4 col-xs-4">
